INSERT INTO `incident` VALUES (1,'2021-02-14 19:44:48','Incident Title Short','Incident Title Long','Incident Restrictions','Incident Description',0,'2021-02-14 19:44:48','2022-01-01 01:01:01','2021-02-14 19:44:48',1,'55.941924','-3.222138',1),(2,'2021-02-14 19:44:48','COVID Lockdown Level 4','COVID Level 4 measures in place in this region','            <table class=\'pub-i-m-res-table\'>\r\n                <tr>\r\n                    <th colspan=\'2\'>\r\n                        Level 4 Measures\r\n                    </th>\r\n                <tr>\r\n                <tr>\r\n                    <th>\r\n                        Socialising\r\n                    </th>\r\n                    <td>\r\n                        No in-home socialising (limited exceptions)<br>\r\n                        <strong>6</strong> people from <strong>2</strong> households outdoors and in public places\r\n                    </td>\r\n                </tr>\r\n                <tr>\r\n                    <th>\r\n                        Hospitality\r\n                    </th>\r\n                    <td>\r\n                        All venues closed\r\n                    </td>\r\n                </tr>\r\n                <tr>\r\n                    <th>\r\n                        Travel\r\n                    </th>\r\n                    <td>\r\n                        Essential travel only to/from level 3 or 4 areas in Scotland, and to/from rest of the UK<br>\r\n                        Follow rules and advice on international travel\r\n                    </td>\r\n                </tr>\r\n                <tr>\r\n                    <th>\r\n                        Transport\r\n                    </th>\r\n                    <td>\r\n                        No use of public transport, except for essential purposes\r\n                    </td>\r\n                </tr>\r\n                <tr>\r\n                    <th>\r\n                        Shopping\r\n                    </th>\r\n      